                    </label>
                  </li>
                  <li>
                    <label>
                      <input 
                        type="checkbox" 
                        checked={selectedCategories.includes('Rings')}
                        onChange={() => handleCategoryChange('Rings')}
                      />
                      Rings <span>(9)</span>
                    </label>
                  </li>
                  <li>
                    <label>
                      <input 
                        type="checkbox" 
                        checked={selectedCategories.includes('Necklaces')}
                        onChange={() => handleCategoryChange('Necklaces')}
                      />
                      Necklaces <span>(3)</span>
                    </label>
                  </li>
                </ul>
              </div>
            )}
          </div>

          {/* Highlight */}
          <div className="filter-section">
            <div className="filter-header" onClick={() => toggleFilter('highlight')}>
              <h3>Highlight</h3>
              <span className={activeFilters.highlight ? 'minus' : 'plus'}></span>
            </div>
            {activeFilters.highlight && (
              <div className="filter-content">
                <ul className="filter-list">
