import React, { useState, useEffect } from 'react';
import { Link, useParams, useLocation } from 'react-router-dom';
import { FaStar, FaRegStar, FaStarHalfAlt, FaChevronDown, FaChevronUp, FaTh, FaList, FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import './Shop.css';

// Sample product data
const productsData = [
  {
    id: 1,
    name: "Blue Topaz Diamond Engagement Wedding Ring",
    category: "Rings",
    price: { min: 190, max: 210 },
    rating: 4.5,
    image: "/images/products/blue-topaz-ring.jpg",
    discount: 5,
    metal: "Yellow Gold",
    carat: "14KT",
    stone: "Diamond",
    stoneColor: "Blue"
  },
  {
    id: 2,
    name: "Buy Diamond Ed Pendant Yellow Gold / 14KT",
    category: "Necklaces",
    price: { min: 120, max: 190 },
    rating: 5,
    image: "/images/products/diamond-pendant.jpg",
    discount: 5,
    metal: "Yellow Gold",
    carat: "14KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 3,
    name: "Casual Thomas Diamond Ring Yellow Gold",
    category: "Rings",
    price: { min: 140, max: 270 },
    rating: 5,
    image: "/images/products/thomas-diamond-ring.jpg",
    discount: 5,
    metal: "Yellow Gold",
    carat: "18KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 4,
    name: "Beautiful Diamond Stud Eulla Earring Rose Gold",
    category: "Earring",
    price: { min: 150, max: 220 },
    rating: 4,
    image: "/images/products/diamond-stud-earring.jpg",
    discount: 6,
    metal: "Rose Gold",
    carat: "22KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 5,
    name: "Beautiful Diamond Ximena Earring Rose Gold",
    category: "Earring",
    price: { min: 160, max: 230 },
    rating: 4,
    image: "/images/products/ximena-earring.jpg",
    discount: 7,
    metal: "Rose Gold",
    carat: "18KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 6,
    name: "Beautiful Lohan Pendant From Kisna Yellow Gold",
    category: "Necklaces",
    price: { min: 180, max: 250 },
    rating: 4.5,
    image: "/images/products/lohan-pendant.jpg",
    discount: 5,
    metal: "Yellow Gold",
    carat: "24KT",
    stone: "Diamond",
    stoneColor: "White",
    countdown: { hours: 16, minutes: 1, seconds: 23 }
  },
  {
    id: 7,
    name: "Diamond Flower Earrings Silver",
    category: "Earring",
    price: { min: 130, max: 200 },
    rating: 4.5,
    image: "/images/products/flower-earrings.jpg",
    discount: 6,
    metal: "Silver",
    carat: "14KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 8,
    name: "Diamond Chain Earrings Yellow Gold",
    category: "Earring",
    price: { min: 170, max: 240 },
    rating: 4,
    image: "/images/products/chain-earrings.jpg",
    discount: 7,
    metal: "Yellow Gold",
    carat: "22KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 9,
    name: "Diamond Engagement Ring Yellow Gold",
    category: "Rings",
    price: { min: 200, max: 300 },
    rating: 5,
    image: "/images/products/engagement-ring.jpg",
    discount: 5,
    metal: "Yellow Gold",
    carat: "24KT",
    stone: "Diamond",
    stoneColor: "White"
  },
  {
    id: 10,
    name: "Ruby Bracelet Rose Gold",
    category: "Bracelets",
    price: { min: 160, max: 230 },
    rating: 4.5,
    image: "/images/products/ruby-bracelet.jpg",
    metal: "Rose Gold",
    carat: "18KT",
    stone: "Ruby",
    stoneColor: "Red"
  },
  {
    id: 11,
    name: "Aquamarine Brooch White Gold",
    category: "Brooches",
    price: { min: 140, max: 210 },
    rating: 4,
    image: "/images/products/aquamarine-brooch.jpg",
    metal: "White Gold",
    carat: "14KT",
    stone: "Aquamarine",
    stoneColor: "Blue"
  },
  {
    id: 12,
    name: "Zambian Emerald Anklet Silver",
    category: "Anklet",
    price: { min: 120, max: 190 },
    rating: 4.5,
    image: "/images/products/emerald-anklet.jpg",
    metal: "Silver",
    carat: "14KT",
    stone: "Zambian-Emerald",
    stoneColor: "Green"
  }
];

// Filter options data
const filterOptions = {
  categories: [
    { name: "Our Store", count: 24 },
    { name: "Anklet", count: 2 },
    { name: "Bracelets", count: 5 },
    { name: "Brooches", count: 8 },
    { name: "Earring", count: 8 },
    { name: "Rings", count: 7 },
    { name: "Necklaces", count: 5 }
  ],
  carat: [
    { name: "14KT", count: 3 },
    { name: "18KT", count: 4 },
    { name: "22KT", count: 4 },
    { name: "24KT", count: 4 }
  ],
  metal: [
    { name: "Yellow Gold", count: 5 },
    { name: "Rose Gold", count: 5 },
    { name: "Silver", count: 4 },
    { name: "White Gold", count: 1 }
  ],
  stone: [
    { name: "Aquamarine", count: 1 },
    { name: "Diamond", count: 2 },
    { name: "Gemstone", count: 1 },
    { name: "Ruby", count: 2 },
    { name: "Solitaire", count: 2 },
    { name: "Zambian-Emerald", count: 2 }
  ],
  stoneColor: [
    { name: "White", count: 8 },
    { name: "Blue", count: 2 },
    { name: "Red", count: 1 },
    { name: "Green", count: 1 }
  ],
  highlight: [
    "All Products",
    "Best Seller",
    "New Arrivals",
    "Sale"
  ]
};

// Sort options
const sortOptions = [
  { value: "default", label: "Default sorting" },
  { value: "price-low-high", label: "Price: Low to High" },
  { value: "price-high-low", label: "Price: High to Low" },
  { value: "rating", label: "Sort by Rating" },
  { value: "newest", label: "Sort by Newest" }
];

const Shop = () => {
  const { category } = useParams();
  const location = useLocation();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [activeFilters, setActiveFilters] = useState({
    categories: [],
    carat: [],
    metal: [],
    stone: [],
    stoneColor: [],
    highlight: "All Products"
  });
  const [expandedFilters, setExpandedFilters] = useState({
    categories: true,
    carat: true,
    metal: true,
    stone: true,
    stoneColor: true,
    highlight: true
  });
  const [sortBy, setSortBy] = useState("default");
  const [viewMode, setViewMode] = useState("grid");
  const [currentPage, setCurrentPage] = useState(1);
  const [productsPerPage] = useState(9);
  const [isMobileFilterOpen, setIsMobileFilterOpen] = useState(false);

  // Initialize products
  useEffect(() => {
    setProducts(productsData);
  }, []);

  // Apply category filter from URL if present
  useEffect(() => {
    if (category) {
      setActiveFilters(prev => ({
        ...prev,
        categories: [category]
      }));
    }
  }, [category]);

  // Apply filters and sorting
  useEffect(() => {
    let result = [...products];

    // Apply category filter
    if (activeFilters.categories.length > 0 && !activeFilters.categories.includes("Our Store")) {
      result = result.filter(product => 
        activeFilters.categories.includes(product.category)
      );
    }

    // Apply carat filter
    if (activeFilters.carat.length > 0) {
      result = result.filter(product => 
        activeFilters.carat.includes(product.carat)
      );
    }

    // Apply metal filter
    if (activeFilters.metal.length > 0) {
      result = result.filter(product => 
        activeFilters.metal.includes(product.metal)
      );
    }

    // Apply stone filter
    if (activeFilters.stone.length > 0) {
      result = result.filter(product => 
        activeFilters.stone.includes(product.stone)
      );
    }

    // Apply stone color filter
    if (activeFilters.stoneColor.length > 0) {
      result = result.filter(product => 
        activeFilters.stoneColor.includes(product.stoneColor)
      );
    }

    // Apply highlight filter
    if (activeFilters.highlight === "Best Seller") {
      result = result.filter(product => product.rating >= 4.5);
    } else if (activeFilters.highlight === "Sale") {
      result = result.filter(product => product.discount);
    }

    // Apply sorting
    if (sortBy === "price-low-high") {
      result.sort((a, b) => a.price.min - b.price.min);
    } else if (sortBy === "price-high-low") {
      result.sort((a, b) => b.price.max - a.price.max);
    } else if (sortBy === "rating") {
      result.sort((a, b) => b.rating - a.rating);
    }

    setFilteredProducts(result);
    setCurrentPage(1);
  }, [products, activeFilters, sortBy]);

  // Toggle filter expansion
  const toggleFilter = (filterName) => {
    setExpandedFilters(prev => ({
      ...prev,
      [filterName]: !prev[filterName]
    }));
  };

  // Handle filter changes
  const handleFilterChange = (filterType, value) => {
    setActiveFilters(prev => {
      const newFilters = { ...prev };
      
      if (filterType === "highlight") {
        newFilters.highlight = value;
      } else {
        if (newFilters[filterType].includes(value)) {
          newFilters[filterType] = newFilters[filterType].filter(item => item !== value);
        } else {
          newFilters[filterType] = [...newFilters[filterType], value];
        }
      }
      
      return newFilters;
    });
  };

  // Handle sort change
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // Handle view mode change
  const toggleViewMode = (mode) => {
    setViewMode(mode);
  };

  // Pagination logic
  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);
  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Render star ratings
  const renderStarRating = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;
    
    for (let i = 1; i <= 5; i++) {
      if (i <= fullStars) {
        stars.push(<FaStar key={i} className="star-icon filled" />);
      } else if (i === fullStars + 1 && hasHalfStar) {
        stars.push(<FaStarHalfAlt key={i} className="star-icon half" />);
      } else {
        stars.push(<FaRegStar key={i} className="star-icon" />);
      }
    }
    
    return stars;
  };

  // Toggle mobile filter sidebar
  const toggleMobileFilter = () => {
    setIsMobileFilterOpen(!isMobileFilterOpen);
  };

  return (
    <div className="shop-page">
      {/* Shop Header */}
      <div className="shop-header">
        <div className="container">
          <div className="breadcrumb">
            <Link to="/">Home</Link> / <span>Shop</span>
          </div>
          <h1>Shop</h1>
        </div>
      </div>

      <div className="shop-container container">
        {/* Mobile Filter Toggle */}
        <button className="mobile-filter-toggle" onClick={toggleMobileFilter}>
          Filters {isMobileFilterOpen ? <FaChevronUp /> : <FaChevronDown />}
        </button>

        {/* Shop Sidebar */}
        <div className={`shop-sidebar ${isMobileFilterOpen ? 'mobile-open' : ''}`}>
          {/* Filter by Categories */}
          <div className="filter-section">
            <div 
              className="filter-header" 
              onClick={() => toggleFilter('categories')}
            >
              <h3>Filter By Categories</h3>
              {expandedFilters.categories ? <FaChevronUp /> : <FaChevronDown />}
            </div>
            {expandedFilters.categories && (
              <div className="filter-content">
                {filterOptions.categories.map((item, index) => (
                  <div className="filter-item" key={index}>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={activeFilters.categories.includes(item.name)}
                        onChange={() => handleFilterChange('categories', item.name)}
                      />
                      <span>{item.name} ({item.count})</span>
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Filter by Carat */}
          <div className="filter-section">
            <div 
              className="filter-header" 
              onClick={() => toggleFilter('carat')}
            >
              <h3>Filter By Carat</h3>
              {expandedFilters.carat ? <FaChevronUp /> : <FaChevronDown />}
            </div>
            {expandedFilters.carat && (
              <div className="filter-content">
                {filterOptions.carat.map((item, index) => (
                  <div className="filter-item" key={index}>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={activeFilters.carat.includes(item.name)}
                        onChange={() => handleFilterChange('carat', item.name)}
                      />
                      <span>{item.name} ({item.count})</span>
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Filter by Metal */}
          <div className="filter-section">
            <div 
              className="filter-header" 
              onClick={() => toggleFilter('metal')}
            >
              <h3>Filter By Choice Of Metal</h3>
              {expandedFilters.metal ? <FaChevronUp /> : <FaChevronDown />}
            </div>
            {expandedFilters.metal && (
              <div className="filter-content">
                {filterOptions.metal.map((item, index) => (
                  <div className="filter-item" key={index}>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={activeFilters.metal.includes(item.name)}
                        onChange={() => handleFilterChange('metal', item.name)}
                      />
                      <span>{item.name} ({item.count})</span>
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Filter by Stone */}
          <div className="filter-section">
            <div 
              className="filter-header" 
              onClick={() => toggleFilter('stone')}
            >
              <h3>Filter By Choice Of Stone</h3>
              {expandedFilters.stone ? <FaChevronUp /> : <FaChevronDown />}
            </div>
            {expandedFilters.stone && (
              <div className="filter-content">
                {filterOptions.stone.map((item, index) => (
                  <div className="filter-item" key={index}>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={activeFilters.stone.includes(item.name)}
                        onChange={() => handleFilterChange('stone', item.name)}
                      />
                      <span>{item.name} ({item.count})</span>
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Filter by Stone Color */}
          <div className="filter-section">
            <div 
              className="filter-header" 
              onClick={() => toggleFilter('stoneColor')}
            >
              <h3>Filter By Stone Color</h3>
              {expandedFilters.stoneColor ? <FaChevronUp /> : <FaChevronDown />}
            </div>
            {expandedFilters.stoneColor && (
              <div className="filter-content">
                {filterOptions.stoneColor.map((item, index) => (
                  <div className="filter-item" key={index}>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={activeFilters.stoneColor.includes(item.name)}
                        onChange={() => handleFilterChange('stoneColor', item.name)}
                      />
                      <span>{item.name} ({item.count})</span>
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Highlight Section */}
          <div className="filter-section">
            <div 
              className="filter-header" 
              onClick={() => toggleFilter('highlight')}
            >
              <h3>Highlight</h3>
              {expandedFilters.highlight ? <FaChevronUp /> : <FaChevronDown />}
            </div>
            {expandedFilters.highlight && (
              <div className="filter-content">
                {filterOptions.highlight.map((item, index) => (
                  <div className="filter-item" key={index}>
                    <label className="radio-label">
                      <input 
                        type="radio" 
                        name="highlight" 
                        checked={activeFilters.highlight === item}
                        onChange={() => handleFilterChange('highlight', item)}
                      />
                      <span>{item}</span>
                    </label>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Shop Content */}
        <div className="shop-content">
          {/* Shop Toolbar */}
          <div className="shop-toolbar">
            <div className="showing-results">
              Showing {indexOfFirstProduct + 1}{Math.min(indexOfLastProduct, filteredProducts.length)} of {filteredProducts.length} results
            </div>
            <div className="toolbar-actions">
              <div className="sort-by">
                <select value={sortBy} onChange={handleSortChange}>
                  {sortOptions.map((option, index) => (
                    <option key={index} value={option.value}>{option.label}</option>
                  ))}
                </select>
              </div>
              <div className="view-mode">
                <button 
                  className={`view-btn ${viewMode === 'grid' ? 'active' : ''}`}
                  onClick={() => toggleViewMode('grid')}
                >
                  <FaTh />
                </button>
                <button 
                  className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
                  onClick={() => toggleViewMode('list')}
                >
                  <FaList />
                </button>
              </div>
            </div>
          </div>

          {/* Products Grid */}
          <div className={`products-container ${viewMode}`}>
            {currentProducts.length > 0 ? (
              currentProducts.map(product => (
                <div className="product-card" key={product.id}>
                  <div className="product-image">
                    <Link to={`/product/${product.id}`}>
                      <img src={product.image || "/placeholder.svg"} alt={product.name} />
                      {product.discount && (
                        <span className="discount-badge">-{product.discount}%</span>
                      )}
                    </Link>
                  </div>
                  <div className="product-info">
                    <h3 className="product-title">
                      <Link to={`/product/${product.id}`}>{product.name}</Link>
                    </h3>
                    <div className="product-rating">
                      {renderStarRating(product.rating)}
                    </div>
                    <div className="product-price">
                      ${product.price.min}  ${product.price.max}
                    </div>
                    {product.countdown && (
                      <div className="product-countdown">
                        <span>{product.countdown.hours}h</span> : <span>{product.countdown.minutes}m</span> : <span>{product.countdown.seconds}s</span>
                      </div>
                    )}
                    {viewMode === 'list' && (
                      <div className="product-description">
                        <p>Beautiful {product.stone} jewelry crafted in {product.metal} ({product.carat}). This exquisite piece features premium quality stones and expert craftsmanship.</p>
                        <button className="add-to-cart-btn">Add to Cart</button>
                      </div>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <div className="no-products">
                <p>No products found matching your criteria. Please try different filters.</p>
              </div>
            )}
          </div>

          {/* Pagination */}
          {filteredProducts.length > productsPerPage && (
            <div className="pagination">
              <button 
                className="page-btn prev" 
                onClick={() => paginate(currentPage - 1)}
                disabled={currentPage === 1}
              >
                <FaChevronLeft />
              </button>
              {Array.from({ length: totalPages }, (_, i) => i + 1).map(number => (
                <button
                  key={number}
                  className={`page-btn ${currentPage === number ? 'active' : ''}`}
                  onClick={() => paginate(number)}
                >
                  {number}
                </button>
              ))}
              <button 
                className="page-btn next" 
                onClick={() => paginate(currentPage + 1)}
                disabled={currentPage === totalPages}
              >
                <FaChevronRight />
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Shop;
