import React, { useState, useEffect, useCallback, useMemo, memo } from "react";
import { Link, useParams, useNavigate, useLocation } from "react-router-dom";
import "./Shop.css";

// Import icons
import {
  FaHeart,
  FaSearch,
  FaShoppingCart,
  FaStar,
  FaStarHalf,
} from "react-icons/fa";
import { BsGrid, BsListUl } from "react-icons/bs";

// Memoized Product Card Component for better performance
const ProductCard = memo(({ product, viewMode, renderStars, quickView, addToWishlist, addToCart }) => {
  return (
    <div className="product-card">
      <Link to={`/product/${product.id}`} className="product-link">
        <div className="product-image">
          {/* Use loading="lazy" for better image loading performance */}
          <img
            src={product.image || "/public/featured1.jpeg"}
            alt={product.name}
            loading="lazy"
            width="100%"
            height="100%"
          />

          {product.isSale && (
            <div className="product-badge sale">
              <span>{product.discount}%</span>
            </div>
          )}

          {product.isNew && (
            <div className="product-badge new">
              <span>NEW</span>
            </div>
          )}

          <div className="product-actions">
            <button
              className="action-btn"
              title="Quick View"
              onClick={(e) => quickView(e, product)}
              aria-label="Quick view"
            >
              <FaSearch />
            </button>
            <button
              className="action-btn"
              title="Add to Wishlist"
              onClick={(e) => addToWishlist(e, product)}
              aria-label="Add to wishlist"
            >
              <FaHeart />
            </button>
          </div>
        </div>

        <div className="product-info">
          <div className="product-category">{product.category}</div>
          <h3 className="product-name">{product.name}</h3>

          <div className="product-rating">
            <div className="stars">{renderStars(product.rating)}</div>
          </div>

          <div className="product-price">
            {product.price.min === product.price.max ? (
              <>
                {product.originalPrice && (
                  <span className="old-price">${product.originalPrice}</span>
                )}
                <span className="current-price">${product.price.min}</span>
              </>
            ) : (
              <span className="price-range">
                ${product.price.min}  ${product.price.max}
              </span>
            )}
          </div>

          <div className="product-description">
            <p>{product.description}</p>
          </div>

          <button
            className="add-to-cart"
            onClick={(e) => addToCart(e, product)}
            aria-label="Add to cart"
          >
            <FaShoppingCart className="cart-icon" /> Add to Cart
          </button>
        </div>
      </Link>
    </div>
  );
});

// Memoized Filter Section Component
const FilterSection = memo(({ title, isActive, toggleFilter, children }) => {
  return (
    <div className="filter-section">
      <div className="filter-header" onClick={() => toggleFilter(title.toLowerCase())}>
        <h3>{title}</h3>
        <span className={isActive ? "minus" : "plus"}></span>
      </div>
      {isActive && <div className="filter-content">{children}</div>}
    </div>
  );
});

const Shop = () => {
  const { category } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const initialSort = searchParams.get("sort") || "default";

  // State for filters
  const [activeFilters, setActiveFilters] = useState({
    categories: true,
    highlight: true,
    carat: true,
    metal: true,
    stone: true,
    stoneColor: true,
    stoneShape: true,
    price: true,
    rating: true,
  });

  // State for view mode (grid or list)
  const [viewMode, setViewMode] = useState("grid");

  // State for sorting
  const [sortBy, setSortBy] = useState(initialSort);

  // State for selected filters
  const [selectedCategories, setSelectedCategories] = useState(
    category ? [category] : []
  );
  const [selectedHighlights, setSelectedHighlights] = useState([]);
  const [selectedCarats, setSelectedCarats] = useState([]);
  const [selectedMetals, setSelectedMetals] = useState([]);
  const [selectedStones, setSelectedStones] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [selectedShapes, setSelectedShapes] = useState([]);
  const [priceRange, setPriceRange] = useState("all");
  const [selectedRatings, setSelectedRatings] = useState([]);

  // State for products
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 12;
  const [categories, setCategories] = useState([]);
  const [categoryCount, setCategoryCount] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  // Toggle filter sections - memoized to prevent unnecessary re-renders
  const toggleFilter = useCallback((filter) => {
    setActiveFilters(prev => ({
      ...prev,
      [filter]: !prev[filter],
    }));
  }, []);

  // Handle category selection - memoized
  const handleCategoryChange = useCallback((category) => {
    navigate(`/shop/${category.toLowerCase()}`);
  }, [navigate]);

  // Handle highlight selection - memoized
  const handleHighlightChange = useCallback((highlight) => {
    setSelectedHighlights(prev => 
      prev.includes(highlight) 
        ? prev.filter(h => h !== highlight) 
        : [...prev, highlight]
    );
  }, []);

  // Handle carat selection - memoized
  const handleCaratChange = useCallback((carat) => {
    setSelectedCarats(prev => 
      prev.includes(carat) 
        ? prev.filter(c => c !== carat) 
        : [...prev, carat]
    );
  }, []);

  // Handle metal selection - memoized
  const handleMetalChange = useCallback((metal) => {
    setSelectedMetals(prev => 
      prev.includes(metal) 
        ? prev.filter(m => m !== metal) 
        : [...prev, metal]
    );
  }, []);

  // Handle stone selection - memoized
  const handleStoneChange = useCallback((stone) => {
    setSelectedStones(prev => 
      prev.includes(stone) 
        ? prev.filter(s => s !== stone) 
        : [...prev, stone]
    );
  }, []);

  // Handle color selection - memoized
  const handleColorChange = useCallback((color) => {
    setSelectedColors(prev => 
      prev.includes(color) 
        ? prev.filter(c => c !== color) 
        : [...prev, color]
    );
  }, []);

  // Handle shape selection - memoized
  const handleShapeChange = useCallback((shape) => {
    setSelectedShapes(prev => 
      prev.includes(shape) 
        ? prev.filter(s => s !== shape) 
        : [...prev, shape]
    );
  }, []);

  // Handle price range selection - memoized
  const handlePriceChange = useCallback((range) => {
    setPriceRange(range);
  }, []);

  // Handle rating selection - memoized
  const handleRatingChange = useCallback((rating) => {
    setSelectedRatings(prev => 
      prev.includes(rating) 
        ? prev.filter(r => r !== rating) 
        : [...prev, rating]
    );
  }, []);

  // Handle sort change - memoized
  const handleSortChange = useCallback((value) => {
    setSortBy(value);
    const newSearchParams = new URLSearchParams(location.search);
    newSearchParams.set("sort", value);
    navigate(`${location.pathname}?${newSearchParams.toString()}`);
  }, [location.pathname, location.search, navigate]);

  // Fetch product data
  useEffect(() => {
    const fetchProducts = async () => {
      setIsLoading(true);
      try {
        // In a real app, this would be an API call
        // For now, we'll simulate a fetch with a timeout
        setTimeout(() => {
          // Product data would come from an API
          const productData = [
            {
              id: 1,
              name: "Beautiful Diamond Stud Earle Earring Rose Gold",
              price: { min: 210, max: 350 },
              rating: 4.5,
              image: "/ragipanis imgs/earrings/earring1.jpg",
              category: "Earrings",
              metal: "Rose Gold",
              stone: "Diamond",
              carat: "18KT",
              isNew: false,
              isSale: false,
              discount: 0,
              description:
                "These beautiful diamond stud earrings are crafted in rose gold with a secure backing. Perfect for everyday wear or special occasions.",
            },
            // ... other products
            {
              id: 33,
              name: "Beautiful Diamond Stud Earle Earring Rose Gold",
              price: { min: 210, max: 350 },
              rating: 4.5,
              image: "/ragipanis imgs/earrings/earring22.jpg",
              category: "Earrings",
              metal: "Rose Gold",
              stone: "Diamond",
              carat: "18KT",
              isNew: false,
              isSale: false,
              discount: 0,
              description:
                "These beautiful diamond stud earrings are crafted in rose gold with a secure backing. Perfect for everyday wear or special occasions.",
            },
          ];

          setProducts(productData);

          // Extract unique categories and count products in each category
          const uniqueCategories = [...new Set(productData.map(product => product.category))];
          setCategories(uniqueCategories);

          const counts = {};
          productData.forEach(product => {
            counts[product.category] = (counts[product.category] || 0) + 1;
          });
          setCategoryCount(counts);

          setIsLoading(false);
        }, 300);
      } catch (error) {
        console.error("Error fetching products:", error);
        setIsLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Apply category filter from URL
  useEffect(() => {
    if (category) {
      setSelectedCategories([category]);
    }
  }, [category]);

  // Apply filters and sorting - memoized with useMemo for performance
  useEffect(() => {
    const applyFiltersAndSort = () => {
      let result = [...products];

      // Apply category filter
      if (selectedCategories.length > 0) {
        result = result.filter(product =>
          selectedCategories.some(cat => product.category.toLowerCase() === cat.toLowerCase())
        );
      }

      // Apply highlight filters
      if (selectedHighlights.length > 0) {
        result = result.filter(product => {
          if (selectedHighlights.includes("New Arrivals") && product.isNew) return true;
          if (selectedHighlights.includes("Sale") && product.isSale) return true;
          if (selectedHighlights.includes("Best Seller") && product.rating >= 4.5) return true;
          return false;
        });
      }

      // Apply carat filter
      if (selectedCarats.length > 0) {
        result = result.filter(product => selectedCarats.includes(product.carat));
      }

      // Apply metal filter
      if (selectedMetals.length > 0) {
        result = result.filter(product => selectedMetals.includes(product.metal));
      }

      // Apply stone filter
      if (selectedStones.length > 0) {
        result = result.filter(product => selectedStones.includes(product.stone));
      }

      // Apply price range filter
      if (priceRange !== "all") {
        const [min, max] = priceRange.split("-").map(Number);
        result = result.filter(product => {
          const price = product.price.min;
          if (max) {
            return price >= min && price <= max;
          } else {
            return price >= min;
          }
        });
      }

      // Apply rating filter
      if (selectedRatings.length > 0) {
        result = result.filter(product => {
          return selectedRatings.some(rating => {
            const ratingNum = parseInt(rating);
            return Math.floor(product.rating) === ratingNum;
          });
        });
      }

      // Apply sorting
      switch (sortBy) {
        case "price-low":
          result.sort((a, b) => a.price.min - b.price.min);
          break;
        case "price-high":
          result.sort((a, b) => b.price.min - a.price.min);
          break;
        case "rating":
          result.sort((a, b) => b.rating - a.rating);
          break;
        case "popularity":
          result.sort((a, b) => b.rating - a.rating);
          break;
        case "date":
          result.sort((a, b) => (a.isNew === b.isNew ? 0 : a.isNew ? -1 : 1));
          break;
        default:
          result.sort((a, b) => a.id - b.id);
      }

      setFilteredProducts(result);
      setCurrentPage(1); // Reset to first page when filters change
    };

    applyFiltersAndSort();
  }, [
    products,
    selectedCategories,
    selectedHighlights,
    selectedCarats,
    selectedMetals,
    selectedStones,
    selectedColors,
    selectedShapes,
    priceRange,
    selectedRatings,
    sortBy,
  ]);

  // Pagination calculations - memoized for performance
  const paginationData = useMemo(() => {
    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);
    const totalPages = Math.ceil(filteredProducts.length / productsPerPage);

    return {
      indexOfFirstProduct,
      indexOfLastProduct,
      currentProducts,
      totalPages
    };
  }, [filteredProducts, currentPage, productsPerPage]);

  // Change page - memoized
  const paginate = useCallback((pageNumber) => {
    setCurrentPage(pageNumber);
    // Scroll to top of product grid when changing pages
    document.querySelector('.products-grid').scrollIntoView({ behavior: 'smooth' });
  }, []);

  // Render star ratings - memoized
  const renderStars = useCallback((rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 1; i <= 5; i++) {
      if (i <= fullStars) {
        stars.push(<FaStar key={i} className="star filled" />);
      } else if (i === fullStars + 1 && hasHalfStar) {
        stars.push(<FaStarHalf key={i} className="star filled" />);
      } else {
        stars.push(<FaStar key={i} className="star" />);
      }
    }

    return stars;
  }, []);

  // Add to cart function - memoized
  const addToCart = useCallback((e, product) => {
    e.preventDefault();
    e.stopPropagation();
    console.log(`Added ${product.name} to cart`);
    // In a real app, you would dispatch an action to add the product to the cart
    alert(`Added ${product.name} to cart!`);
  }, []);

  // Add to wishlist function - memoized
  const addToWishlist = useCallback((e, product) => {
    e.preventDefault();
    e.stopPropagation();
    console.log(`Added ${product.name} to wishlist`);
    // In a real app, you would dispatch an action to add the product to the wishlist
    alert(`Added ${product.name} to wishlist!`);
  }, []);

  // Quick view function - memoized
  const quickView = useCallback((e, product) => {
    e.preventDefault();
    e.stopPropagation();
    console.log(`Quick view for ${product.name}`);
    // In a real app, you would open a modal with product details
    alert(`Quick view for ${product.name}`);
  }, []);

  // Memoize carat counts for better performance
  const caratCounts = useMemo(() => {
    return {
      "14KT": products.filter(p => p.carat === "14KT").length,
      "18KT": products.filter(p => p.carat === "18KT").length,
      "22KT": products.filter(p => p.carat === "22KT").length,
      "24KT": products.filter(p => p.carat === "24KT").length,
    };
  }, [products]);

  // Memoize metal counts
  const metalCounts = useMemo(() => {
    return {
      "Yellow Gold": products.filter(p => p.metal === "Yellow Gold").length,
      "Rose Gold": products.filter(p => p.metal === "Rose Gold").length,
      "White Gold": products.filter(p => p.metal === "White Gold").length,
      "Platinum": products.filter(p => p.metal === "Platinum").length,
    };
  }, [products]);

  // Memoize stone counts
  const stoneCounts = useMemo(() => {
    return {
      "Diamond": products.filter(p => p.stone === "Diamond").length,
      "Ruby": products.filter(p => p.stone === "Ruby").length,
      "Sapphire": products.filter(p => p.stone === "Sapphire").length,
      "Topaz": products.filter(p => p.stone === "Topaz").length,
      "Pearl": products.filter(p => p.stone === "Pearl").length,
    };
  }, [products]);

  // Memoize rating counts
  const ratingCounts = useMemo(() => {
    return {
      "5": products.filter(p => Math.floor(p.rating) === 5).length,
      "4": products.filter(p => Math.floor(p.rating) === 4).length,
      "3": products.filter(p => Math.floor(p.rating) === 3).length,
      "2": products.filter(p => Math.floor(p.rating) === 2).length,
      "1": products.filter(p => Math.floor(p.rating) === 1).length,
    };
  }, [products]);

  return (
    <div className="shop-page">
      {/* Page Header */}
      <div className="shop-header">
        <div className="container">
          <div className="breadcrumb">
            <Link to="/">Home</Link> /<Link to="/shop">Shop</Link>
            {category && (
              <>
                {" "}
                / <span>{category}</span>
              </>
            )}
          </div>
          <h1>{category || "Shop"}</h1>
        </div>
      </div>

      <div className="shop-container container">
        {/* Mobile Filter Toggle */}
        <button
          className="mobile-filter-toggle"
          onClick={() => document.body.classList.toggle("filters-active")}
          aria-label="Toggle filters"
        >
          Filter Products
        </button>

        {/* Shop Sidebar */}
        <div className="shop-sidebar">
          <div className="sidebar-header">
            <h3>Filters</h3>
            <button
              className="close-filters"
              onClick={() => document.body.classList.remove("filters-active")}
              aria-label="Close filters"
            >
              
            </button>
          </div>

          {/* Filter by Categories */}
          <FilterSection 
            title="Filter By Categories" 
            isActive={activeFilters.categories} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list">
              <li>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedCategories.length === 0}
                    onChange={() => {
                      if (selectedCategories.length === 0) {
                        setSelectedCategories(categories);
                      } else {
                        setSelectedCategories([]);
                      }
                    }}
                  />
                  All Products <span>({products.length})</span>
                </label>
              </li>
              {categories.map((cat) => (
                <li key={cat}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedCategories.includes(cat)}
                      onChange={() => handleCategoryChange(cat)}
                    />
                    {cat} <span>({categoryCount[cat] || 0})</span>
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>

          {/* Highlight */}
          <FilterSection 
            title="Highlight" 
            isActive={activeFilters.highlight} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list">
              <li>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedHighlights.includes("Best Seller")}
                    onChange={() => handleHighlightChange("Best Seller")}
                  />
                  Best Seller
                </label>
              </li>
              <li>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedHighlights.includes("New Arrivals")}
                    onChange={() => handleHighlightChange("New Arrivals")}
                  />
                  New Arrivals
                </label>
              </li>
              <li>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedHighlights.includes("Sale")}
                    onChange={() => handleHighlightChange("Sale")}
                  />
                  Sale
                </label>
              </li>
            </ul>
          </FilterSection>

          {/* Filter by Carat */}
          <FilterSection 
            title="Filter By Carat" 
            isActive={activeFilters.carat} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list">
              {["14KT", "18KT", "22KT", "24KT"].map((carat) => (
                <li key={carat}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedCarats.includes(carat)}
                      onChange={() => handleCaratChange(carat)}
                    />
                    {carat} <span>({caratCounts[carat] || 0})</span>
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>

          {/* Filter by Choice Of Metal */}
          <FilterSection 
            title="Filter By Choice Of Metal" 
            isActive={activeFilters.metal} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list">
              {Object.entries(metalCounts).map(([metal, count]) => (
                <li key={metal}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedMetals.includes(metal)}
                      onChange={() => handleMetalChange(metal)}
                    />
                    {metal} <span>({count})</span>
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>

          {/* Filter by Choice Of Stone */}
          <FilterSection 
            title="Filter By Choice Of Stone" 
            isActive={activeFilters.stone} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list">
              {Object.entries(stoneCounts).map(([stone, count]) => (
                <li key={stone}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedStones.includes(stone)}
                      onChange={() => handleStoneChange(stone)}
                    />
                    {stone} <span>({count})</span>
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>

          {/* Filter by Stone Color */}
          <FilterSection 
            title="Filter By Stone Color" 
            isActive={activeFilters.stoneColor} 
            toggleFilter={toggleFilter}
          >
            <div className="color-options">
              {["blue", "green", "red", "purple", "yellow", "white"].map(color => (
                <button
                  key={color}
                  className={`color-option ${color} ${
                    selectedColors.includes(color) ? "selected" : ""
                  }`}
                  onClick={() => handleColorChange(color)}
                  aria-label={`Select ${color} color`}
                  aria-pressed={selectedColors.includes(color)}
                ></button>
              ))}
            </div>
          </FilterSection>

          {/* Filter by Stone Shape */}
          <FilterSection 
            title="Filter By Stone Shape" 
            isActive={activeFilters.stoneShape} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list shape-list">
              {["cushion", "emerald", "oval", "pear", "princess", "round"].map(shape => (
                <li key={shape}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedShapes.includes(shape)}
                      onChange={() => handleShapeChange(shape)}
                    />
                    <span className={`shape ${shape}`}></span> {shape.charAt(0).toUpperCase() + shape.slice(1)}
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>

          {/* Price Filter */}
          <FilterSection 
            title="Price Filter" 
            isActive={activeFilters.price} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list">
              {[
                { value: "all", label: "All" },
                { value: "0-100", label: "$0 - $100" },
                { value: "100-200", label: "$100 - $200" },
                { value: "200-300", label: "$200 - $300" },
                { value: "300-400", label: "$300 - $400" },
                { value: "400-500", label: "$400 - $500" },
                { value: "500-", label: "$500+" }
              ].map(range => (
                <li key={range.value}>
                  <label>
                    <input
                      type="radio"
                      name="price"
                      checked={priceRange === range.value}
                      onChange={() => handlePriceChange(range.value)}
                    />
                    {range.label}
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>

          {/* Average Rating */}
          <FilterSection 
            title="Average Rating" 
            isActive={activeFilters.rating} 
            toggleFilter={toggleFilter}
          >
            <ul className="filter-list rating-list">
              {[5, 4, 3, 2, 1].map(rating => (
                <li key={rating}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedRatings.includes(rating.toString())}
                      onChange={() => handleRatingChange(rating.toString())}
                    />
                    <div className="stars">
                      {Array.from({ length: 5 }, (_, i) => (
                        <FaStar 
                          key={i} 
                          className={`star ${i < rating ? 'filled' : ''}`} 
                        />
                      ))}
                    </div>
                    <span>({ratingCounts[rating] || 0})</span>
                  </label>
                </li>
              ))}
            </ul>
          </FilterSection>
        </div>

        <div className="shop-content">
          <div className="shop-toolbar">
            <div className="showing-results">
              {isLoading ? (
                "Loading products..."
              ) : (
                <>
                  Showing {paginationData.indexOfFirstProduct + 1}
                  {Math.min(paginationData.indexOfLastProduct, filteredProducts.length)} of{" "}
                  {filteredProducts.length} results
                </>
              )}
            </div>
            <div className="shop-actions">
              <select
                className="sort-select"
                value={sortBy}
                onChange={(e) => handleSortChange(e.target.value)}
                aria-label="Sort products"
              >
                <option value="default">Default sorting</option>
                <option value="popularity">Sort by popularity</option>
                <option value="rating">Sort by average rating</option>
                <option value="date">Sort by latest</option>
                <option value="price-low">Sort by price: low to high</option>
                <option value="price-high">Sort by price: high to low</option>
              </select>
              <div className="view-switcher">
                <button
                  className={`view-btn ${viewMode === "grid" ? "active" : ""}`}
                  onClick={() => setViewMode("grid")}
                  aria-label="Grid view"
                  aria-pressed={viewMode === "grid"}
                >
                  <BsGrid />
                </button>
                <button
                  className={`view-btn ${viewMode === "list" ? "active" : ""}`}
                  onClick={() => setViewMode("list")}
                  aria-label="List view"
                  aria-pressed={viewMode === "list"}
                >
                  <BsListUl />
                </button>
              </div>
            </div>
          </div>

          {isLoading ? (
            <div className="loading-products">
              <p>Loading products...</p>
            </div>
          ) : (
            <div
              className={`products-grid ${viewMode === "list" ? "list-view" : ""}`}
              role="region"
              aria-label="Products"
            // className={`products-grid ${viewMode === "list" ? "list-view" : ""}`}
            //   role="region"
            //   aria-label="Products"
            >
              {paginationData.currentProducts.length > 0 ? (
                paginationData.currentProducts.map((product) => (
                  <ProductCard
                    key={product.id}
                    product={product}
                    viewMode={viewMode}
                    renderStars={renderStars}
                    quickView={quickView}
                    addToWishlist={addToWishlist}
                    addToCart={addToCart}
                  />
                ))
              ) : (
                <div className="no-products">
                  <p>
                    No products found matching your criteria. Please try different
                    filters.
                  </p>
                </div>
              )}
            </div>
          )}

          {/* Pagination */}
          {!isLoading && paginationData.totalPages > 1 && (
            <div className="pagination" role="navigation" aria-label="Pagination">
              {/* Previous page button */}
              {currentPage > 1 && (
                <button
                  onClick={() => paginate(currentPage - 1)}
                  className="prev page-numbers"
                  aria-label="Previous page"
                >
                  
                </button>
              )}
              
              {/* Page numbers */}
              {Array.from({ length: paginationData.totalPages }, (_, i) => {
                // Show limited page numbers for better UX with many pages
                const pageNum = i + 1;
                const showPageNumber = 
                  pageNum === 1 || 
                  pageNum === paginationData.totalPages || 
                  (pageNum >= currentPage - 1 && pageNum <= currentPage + 1);
                
                if (showPageNumber) {
                  return (
                    <button
                      key={pageNum}
                      onClick={() => paginate(pageNum)}
                      className={currentPage === pageNum ? "current" : ""}
                      aria-label={`Page ${pageNum}`}
                      aria-current={currentPage === pageNum ? "page" : undefined}
                    >
                      {pageNum}
                    </button>
                  );
                } else if (
                  (pageNum === currentPage - 2 && currentPage > 3) || 
                  (pageNum === currentPage + 2 && currentPage < paginationData.totalPages - 2)
                ) {
                  // Show ellipsis for skipped pages
                  return <span key={pageNum} className="pagination-ellipsis"></span>;
                }
                return null;
              })}
              
              {/* Next page button */}
              {currentPage < paginationData.totalPages && (
                <button
                  className="next page-numbers"
                  onClick={() => paginate(currentPage + 1)}
                  aria-label="Next page"
                >
                  
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Shop;
